function formatURL(url) {
    let formatted = url.substring(1).replace(/-/g, ' ');
    return formatted.replace(/\b\w/g, char => char.toUpperCase());
}

function filterURLs() {
    const input = document.getElementById('searchInput');
    const filter = input.value.toLowerCase();
    const suggestionsList = document.getElementById('suggestionsList');

    suggestionsList.innerHTML = '';

    if (filter) {
        const filteredURLs = urls.filter(url => url.toLowerCase().includes(filter));

        filteredURLs.forEach(url => {
            const listItem = document.createElement('li');
            listItem.textContent = formatURL(url);
            listItem.onclick = () => {
                window.location.href = url;
            };
            suggestionsList.appendChild(listItem);
        });

        suggestionsList.style.display = filteredURLs.length ? 'block' : 'none';
    } else {
        suggestionsList.style.display = 'none';
    }
}

function navigateOnEnter(event) {
    if (event.key === 'Enter') {
        const input = document.getElementById('searchInput');
        const url = urls.find(url => url.toLowerCase().includes(input.value.toLowerCase()));
        if (url) {
            window.location.href = url;
        }
    }
}